const News = (res) =>{

  return res.map(newItem =>{
    let imgItem = newItem.img
    let info= JSON.parse(newItem.category)


    return `
       <div  class="market__main__card" onclick="SaveItem()" set=${newItem.id}>
        <div class="market__main__card__header">
        <img class="market__main__card__header__img style="width:500px"  src=${imgItem}  "/>
        <div class="market__main__card__header__titile"><span class=${info.className}>${info.name}</span></div>
    </div>
    <div class="market__main__card__main">
        <div class="market__main__card__main__data">${newItem.pud_date}</div>
        <div class="market__main__card__main__title ">
            <a href="/news-item.html#${newItem.id}">
                ${newItem.title}
            </a>
        </div>
        <div class="market__main__card__main__subtitle">
              ${newItem.description.slice(0,50) + "..."}
        </div>
    </div>
    </div>
  
         `
  })
};

function SaveItem (item) {
  localStorage.setItem('key', item)
}

